void dequeues()
{
    int i = 0;
        len -= 1;
        while (i < len)
        {
                list[i] = list[i + 1];
                i++;
        }
        list[i] = 0;
}


void enqueue(kenteken element)
{
     list[element] = element;
}
void dequeue(kenteken element)
{
     list[element] = 0;
}
